            WHEN ${users.age} < 50 THEN '40-49ì„¸'
            WHEN ${users.age} < 60 THEN '50-59ì„¸'
            WHEN ${users.age} >= 60 THEN '60ì„¸ ì´ìƒ'
            ELSE 'ë¯¸ë“±ë¡'
          END
        `,
        count: count(),
      })
      .from(users)
      .groupBy(sql`
        CASE 
          WHEN ${users.age} < 20 THEN '10-19ì„¸'
          WHEN ${users.age} < 30 THEN '20-29ì„¸'
          WHEN ${users.age} < 40 THEN '30-39ì„¸'
          WHEN ${users.age} < 50 THEN '40-49ì„¸'
          WHEN ${users.age} < 60 THEN '50-59ì„¸'
          WHEN ${users.age} >= 60 THEN '60ì„¸ ì´ìƒ'
          ELSE 'ë¯¸ë“±ë¡'
