  --sidebar: hsl(30, 27%, 18%);
  --sidebar-foreground: hsl(42, 47%, 90%);
  --sidebar-primary: hsl(43, 74%, 55%);
  --sidebar-primary-foreground: hsl(30, 27%, 15%);
  --sidebar-accent: hsl(43, 74%, 55%);
  --sidebar-accent-foreground: hsl(30, 27%, 15%);
  --sidebar-border: hsl(30, 20%, 30%);
  --sidebar-ring: hsl(43, 74%, 55%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-serif antialiased bg-background text-foreground;
    background-image: 
      radial-gradient(circle at 25% 25%, hsl(45, 43%, 98%) 0%, transparent 50%),
      radial-gradient(circle at 75% 75%, hsl(42, 47%, 96%) 0%, transparent 50%);
  }

  .dark body {
    background-image: 
      radial-gradient(circle at 25% 25%, hsl(30, 27%, 20%) 0%, transparent 50%),
      radial-gradient(circle at 75% 75%, hsl(30, 27%, 18%) 0%, transparent 50%);
  }
}

@layer components {
  /* Bible Book Styling */
  .bible-book {
    background: linear-gradient(
      145deg,
      hsl(45, 43%, 96%) 0%,
      hsl(42, 40%, 94%) 50%,
      hsl(40, 35%, 92%) 100%
    );
    box-shadow: 
      0 8px 32px rgba(0, 0, 0, 0.1),
      0 4px 16px rgba(0, 0, 0, 0.05),
      inset 0 1px 0 rgba(255, 255, 255, 0.7);
    border: 2px solid hsl(35, 30%, 85%);
    position: relative;
  }

  .bible-book::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-image: 
      radial-gradient(circle at 25% 25%, rgba(212, 175, 55, 0.03) 0%, transparent 50%),
      radial-gradient(circle at 75% 75%, rgba(139, 69, 19, 0.02) 0%, transparent 50%),
      linear-gradient(90deg, transparent 48%, rgba(139, 69, 19, 0.02) 49%, rgba(139, 69, 19, 0.02) 51%, transparent 52%);
    pointer-events: none;
    border-radius: inherit;
  }

  .dark .bible-book {
    background: linear-gradient(
      145deg,
      hsl(30, 27%, 22%) 0%,
      hsl(30, 25%, 20%) 50%,
      hsl(30, 23%, 18%) 100%
    );
    border: 2px solid hsl(30, 20%, 25%);
  }

  .bible-page {
    background: linear-gradient(
      to bottom,
      hsl(45, 50%, 98%) 0%,
      hsl(42, 45%, 96%) 100%
    );
    border: 1px solid hsl(40, 25%, 88%);
    position: relative;
    overflow: hidden;
  }

  .bible-page::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-image: 
      repeating-linear-gradient(
        0deg,
        transparent,
        transparent 23px,
        rgba(200, 180, 140, 0.05) 24px,
        rgba(200, 180, 140, 0.05) 25px
      );
    pointer-events: none;
  }

  .dark .bible-page {
    background: linear-gradient(
      to bottom,
      hsl(30, 27%, 25%) 0%,
