      // í…Œì´ë¸” ëª©ë¡ ì¡°íšŒ
      this.sqliteDb.all(`
        SELECT name FROM sqlite_master 
        WHERE type='table' AND name NOT LIKE 'sqlite_%'
      `, (err, tables: any[]) => {
        if (err) {
          reject(err);
          return;
        }

        console.log('ðŸ“Š ë°œê²¬ëœ í…Œì´ë¸”ë“¤:');
        tables.forEach(table => console.log(`  - ${table.name}`));

        // ê° í…Œì´ë¸”ì˜ êµ¬ì¡° ë¶„ì„
        let analyzed = 0;
        tables.forEach(table => {
          this.sqliteDb.all(`PRAGMA table_info(${table.name})`, (err, columns: any[]) => {
            if (err) {
              console.error(`âŒ ${table.name} í…Œì´ë¸” ë¶„ì„ ì‹¤íŒ¨:`, err);
            } else {
              console.log(`\nðŸ“‹ ${table.name} í…Œì´ë¸” êµ¬ì¡°:`);
              columns.forEach(col => {
                console.log(`  - ${col.name}: ${col.type} ${col.pk ? '(PRIMARY KEY)' : ''}`);
              });
            }

            analyzed++;
            if (analyzed === tables.length) {
              resolve();
            }
          });
        });
      });
    });
  }

  // ìƒ˜í”Œ ë°ì´í„° ì¡°íšŒ
  async showSampleData(tableName: string, limit: number = 3): Promise<void> {
    return new Promise((resolve, reject) => {
      console.log(`\nðŸ”Ž ${tableName} í…Œì´ë¸” ìƒ˜í”Œ ë°ì´í„° (ìµœëŒ€ ${limit}ê°œ):`);

      this.sqliteDb.all(`SELECT * FROM ${tableName} LIMIT ${limit}`, (err, rows: any[]) => {
        if (err) {
          reject(err);
          return;
        }

        rows.forEach((row, index) => {
          console.log(`\nðŸ“„ Row ${index + 1}:`);
          Object.entries(row).forEach(([key, value]) => {
            console.log(`  ${key}: ${value}`);
