      } catch (err) {
        console.log(`âš ï¸ ${table} í…Œì´ë¸” ì—†ìŒ, ê±´ë„ˆëœ€`);
      }
    }

    // ì„±ê²½ì‚¬ì „ í…Œì´ë¸”ë“¤
    const dictionaryTables = ['dictionary', 'bible_dictionary', 'concordance', 'lexicon'];
    for (const table of dictionaryTables) {
