  console.log(`ðŸ“š ì„±ê²½ì±… ${bookMap.length}ê°œ, ë²ˆì—­ë³¸ ${translationMap.length}ê°œ, ì–¸ì–´ ${languageMap.length}ê°œ ë°œê²¬`);

