                languageId: defaultLanguage.id,
                bookCode: row.book_code,
                chapter: row.chapter || 1,
                verse: row.verse || 1,
                content: row.content
              }).onConflictDoNothing();

              batchMigrated++;
            } catch (insertErr) {
              // ê°œë³„ ì˜¤ë¥˜ëŠ” ì¡°ìš©ížˆ ë„˜ì–´ê°
              console.error(`êµ¬ì ˆ ì‚½ìž… ì‹¤íŒ¨: ${row.book_code} ${row.chapter}:${row.verse}`, insertErr);
            }
          }

          totalMigrated += batchMigrated;
          console.log(`âœ… ë°°ì¹˜ ${Math.floor(offset / batchSize) + 1}: ${batchMigrated}/${rows.length}ê°œ ì´ê´€ (ì´ ${totalMigrated}ê°œ)`);

          offset += batchSize;

          // ë‹¤ìŒ ë°°ì¹˜ ì²˜ë¦¬
          setTimeout(processBatch, 100); // 100ms ë”œë ˆì´ë¡œ DB ë¶€í•˜ ë°©ì§€
        }
      );
    };

    processBatch();
  });
}

async function main() {
  try {
    console.log('ðŸš€ êµ¬ì ˆ ì´ê´€ ë¬¸ì œ í•´ê²° ì‹œìž‘!');

    await checkSQLiteVerses();
    await migrateBibleVersesFixed();

    // ì´ê´€ ê²°ê³¼ í™•ì¸
    const verseCount = await db.select().from(bibleVerses);
    console.log(`\nðŸ“Š ìµœì¢… ì´ê´€ëœ êµ¬ì ˆ ìˆ˜: ${verseCount.length}ê°œ`);

  } catch (error) {
    console.error('âŒ êµ¬ì ˆ ì´ê´€ ì‹¤íŒ¨:', error);
  } finally {
    sqliteDb.close();
    process.exit(0);
  }
}

