import { Request, Response, NextFunction } from 'express';

interface RateLimitEntry {
  count: number;
  resetTime: number;
}

class MemoryRateLimiter {
  private store: Map<string, RateLimitEntry> = new Map();
  private readonly windowMs: number;
  private readonly maxRequests: number;

  constructor(windowMs: number = 15 * 60 * 1000, maxRequests: number = 5) {
    this.windowMs = windowMs; // ê¸°ë³¸ 15ë¶„
    this.maxRequests = maxRequests; // ê¸°ë³¸ 5íšŒ

    // ì£¼ê¸°ì ìœ¼ë¡œ ë§Œë£Œëœ í•­ëª© ì •ë¦¬ (5ë¶„ë§ˆë‹¤)
    setInterval(() => this.cleanup(), 5 * 60 * 1000);
  }

  private cleanup(): void {
    const now = Date.now();
    for (const [key, entry] of Array.from(this.store.entries())) {
      if (now > entry.resetTime) {
        this.store.delete(key);
      }
    }
  }

  private getKey(req: Request): string {
    // IP ì£¼ì†Œì™€ User-Agentë¥¼ ì¡°í•©í•˜ì—¬ í‚¤ ìƒì„±
    const ip = req.ip || req.connection.remoteAddress || 'unknown';
    const userAgent = req.get('User-Agent') || 'unknown';
    return `${ip}:${userAgent.substring(0, 50)}`;
  }

  public middleware() {
    return (req: Request, res: Response, next: NextFunction) => {
      const key = this.getKey(req);
      const now = Date.now();

      let entry = this.store.get(key);

      if (!entry || now > entry.resetTime) {
        // ìƒˆ ìœˆë„ìš° ì‹œìž‘
        entry = {
          count: 1,
          resetTime: now + this.windowMs
        };
        this.store.set(key, entry);
        return next();
      }

      if (entry.count >= this.maxRequests) {
        // ìš”ì²­ í•œë„ ì´ˆê³¼
        const retryAfter = Math.ceil((entry.resetTime - now) / 1000);

        res.set({
          'X-RateLimit-Limit': this.maxRequests.toString(),
          'X-RateLimit-Remaining': '0',
          'X-RateLimit-Reset': entry.resetTime.toString(),
          'Retry-After': retryAfter.toString()
        });

        return res.status(429).json({
          message: `ìš”ì²­ì´ ë„ˆë¬´ ë§ŽìŠµë‹ˆë‹¤. ${Math.ceil(retryAfter / 60)}ë¶„ í›„ì— ë‹¤ì‹œ ì‹œë„í•´ì£¼ì„¸ìš”.`,
          retryAfter: retryAfter
        });
      }

      // ìš”ì²­ ì¹´ìš´íŠ¸ ì¦ê°€
      entry.count++;
      this.store.set(key, entry);

      // í—¤ë” ì„¤ì •
      res.set({
        'X-RateLimit-Limit': this.maxRequests.toString(),
        'X-RateLimit-Remaining': (this.maxRequests - entry.count).toString(),
        'X-RateLimit-Reset': entry.resetTime.toString()
      });

      next();
    };
  }
}

// ê° ì—”ë“œí¬ì¸íŠ¸ë³„ ì œí•œ ì„¤ì •
