  15 * 60 * 1000, // 15ë¶„
  5 // ìµœëŒ€ 5íšŒ ì‹œë„
);

export const verifyRateLimiter = new MemoryRateLimiter(
  10 * 60 * 1000, // 10ë¶„
  3 // ìµœëŒ€ 3íšŒ ì‹œë„ (ì´ë©”ì¼ ì¸ì¦ì€ ë” ì—„ê²©í•˜ê²Œ)
);

